import React, { useState } from 'react';
import axios from 'axios';

const Message = ({ receiverId, receiverModel, onClose }) => {
  const [content, setContent] = useState('');
  const [isSending, setIsSending] = useState(false);

  // Get sender info from localStorage
  const user = JSON.parse(localStorage.getItem('user'));
  const senderId = user._id;
  const senderModel = user.role === 'worker' ? 'Worker' : 'User';

  const handleSend = async () => {
  if (!content.trim()) {
    alert('Message cannot be empty');
    return;
  }

  setIsSending(true);
  try {
    // Ensure we're using the correct model names that match your backend
    const response = await axios.post('http://localhost:5000/api/messages', {
      senderId: user._id,
      receiverId,
      senderModel: 'User', // Always use 'User' since you don't have separate Worker model
      receiverModel: 'User', // Same here
      content
    }, {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    });

    if (response.status === 201) {
      alert('Message sent successfully!');
      setContent('');
      onClose();
    }
  } catch (err) {
    console.error('Failed to send message:', {
      error: err.response?.data || err.message,
      config: err.config,
    });
    alert(`Error: ${err.response?.data?.error || 'Failed to send message'}`);
  } finally {
    setIsSending(false);
  }
};
  return (
    <div style={styles.popup}>
      <h3>Send Message</h3>
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Type your message..."
        style={styles.textarea}
        disabled={isSending}
      />
      <div style={styles.actions}>
        <button 
          onClick={handleSend} 
          style={styles.button}
          disabled={isSending || !content.trim()}
        >
          {isSending ? 'Sending...' : 'Send'}
        </button>
        <button 
          onClick={onClose} 
          style={styles.cancel}
          disabled={isSending}
        >
          Cancel
        </button>
      </div>
    </div>
  );
};

const styles = {
  popup: {
    background: '#fff',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0,0,0,0.2)',
    width: '350px',
    maxWidth: '90%',
    position: 'fixed',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    zIndex: 1000,
  },
  textarea: {
    width: '100%',
    height: '120px',
    padding: '10px',
    margin: '10px 0',
    borderRadius: '5px',
    border: '1px solid #ddd',
    resize: 'vertical',
  },
  actions: {
    display: 'flex',
    justifyContent: 'flex-end',
    gap: '10px',
    marginTop: '15px',
  },
  button: {
    padding: '8px 16px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background-color 0.2s',
  },
  cancel: {
    padding: '8px 16px',
    backgroundColor: '#f0f0f0',
    color: '#333',
    border: '1px solid #ddd',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background-color 0.2s',
  },
};

export default Message;
