import React from "react";
import { motion } from "framer-motion";

const HeroSection = () => {
  return (
    <div className="bg-gradient-to-b from-purple-950 to-black min-h-screen flex items-center justify-center px-8">
      <div className="max-w-6xl w-full flex flex-col md:flex-row items-center justify-between">
        
        {/* Left Content */}
        <div className="text-white max-w-lg">
          <div className="flex items-center mb-4">
            <span className="text-yellow-300 text-3xl mr-2">✦</span>
            <h1 className="text-4xl md:text-6xl font-extrabold leading-tight">
              WEBFLOW <br /> TRICKS AND <br /> WIZARDRY
            </h1>
          </div>
          <p className="text-gray-300 mb-8">
            Giving Webflow developers a competitive edge in the industry through interactive tutorials
          </p>
          <button className="bg-white text-purple-700 px-6 py-3 rounded-full font-semibold hover:bg-purple-700 hover:text-white transition duration-300">
            GLASSMORPHISM TUTORIAL →
          </button>
        </div>

        {/* Right Animation */}
        <div className="relative mt-12 md:mt-0">
          {/* Rotating Bubble */}
          <motion.div
            className="w-80 h-80 rounded-full bg-purple-500 blur-3xl opacity-70"
            animate={{ rotate: 360 }}
            transition={{ repeat: Infinity, duration: 10, ease: "linear" }}
          ></motion.div>

          {/* Glass Card */}
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 backdrop-blur-lg bg-white/10 border border-white/30 rounded-xl px-8 py-12 w-72 shadow-2xl">
            <h2 className="text-xl text-white font-bold mb-8">T.RICKS</h2>
            <div className="text-gray-300 text-sm tracking-widest">
              4323 7645 2828 0713
            </div>
          </div>
        </div>

      </div>
    </div>
  );
};

export default HeroSection;
