import React, { useState } from 'react';
import axios from 'axios';

const Message = ({ selectedWorker, currentUserId, onClose }) => {
  const [message, setMessage] = useState('');

  // ✅ Safety check: Don't render if selectedWorker is undefined
  if (!selectedWorker) {
    return null; // or <div>Loading...</div>
  }

  const handleSend = async () => {
    if (!message.trim()) {
      alert('Please enter a message before sending.');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/message', {
        senderId: currentUserId,
        receiverId: selectedWorker._id,
        content: message
      });
      alert('Message sent!');
      setMessage('');
      onClose();
    } catch (err) {
      console.error('Send error', err);
      alert('Failed to send message');
    }
  };

  return (
    <div className="popup">
      <h3>Send Message to {selectedWorker.name || 'Worker'}</h3>
      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type your message here..."
        rows={4}
        style={{ width: '100%', padding: '8px', marginTop: '8px' }}
      />
      <div style={{ marginTop: '10px' }}>
        <button onClick={handleSend} style={{ marginRight: '10px' }}>Send</button>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default Message;
