import React from 'react';
import { motion } from 'framer-motion';

const HeroSection = () => {
  return (
    <div className="bg-gradient-to-b from-[#0d001c] to-black min-h-screen flex items-center justify-center px-6 md:px-12">
      <div className="max-w-7xl w-full flex flex-col md:flex-row items-center justify-between relative">
        
        {/* LEFT SECTION */}
        <div className="text-white z-10">
          <div className="flex items-center space-x-2">
            <span className="text-yellow-300 text-4xl">✦</span>
            <h1 className="text-4xl md:text-6xl font-extrabold leading-tight">
              WEBFLOW <br /> TRICKS AND <br /> WIZARDRY
            </h1>
          </div>
          <p className="mt-6 text-gray-300 max-w-md">
            Giving Webflow developers a competitive edge in the industry through interactive tutorials
          </p>
          <div className="mt-10">
            <button className="flex items-center bg-white text-purple-700 font-bold px-6 py-3 rounded-full hover:bg-purple-700 hover:text-white transition">
              GLASSMORPHISM TUTORIAL <span className="ml-3 text-xl">→</span>
            </button>
          </div>
        </div>

        {/* RIGHT SECTION - BUBBLE + CARD */}
        <div className="relative mt-20 md:mt-0 w-[350px] h-[350px]">
          {/* ROTATING PURPLE BUBBLE */}
          <motion.div
            className="absolute w-full h-full rounded-full bg-purple-600 blur-[100px] opacity-70"
            animate={{ rotate: 360 }}
            transition={{ repeat: Infinity, duration: 15, ease: 'linear' }}
          />

          {/* GLASS CARD */}
          <div className="absolute w-[300px] h-[180px] bg-white/10 border border-white/30 backdrop-blur-xl rounded-xl p-6 flex flex-col justify-between shadow-2xl z-10 transform rotate-[-8deg]">
            <div className="text-white text-lg font-bold">T.RICKS</div>
            <div className="text-white tracking-widest text-sm">4323 7645 2828 0713</div>
            <div className="flex justify-end text-white text-2xl">✦</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
