import React, { useState, useEffect } from 'react';
import workersImage from '../utils/photo1.webp'; // adjust path as per your project

const HeroSection = () => {
  const [blink, setBlink] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setBlink(prev => !prev);
    }, 5000); // blink every 5 seconds

    return () => clearInterval(interval);
  }, []);

  const factsParagraph = `
    üîß Blue-collar workers build our daily world.
    üõ†Ô∏è They form over 60% of India's workforce.
    üöö Without them, supply chains stall overnight.
    ‚ö° Electricians and plumbers keep homes running smoothly.
    üèóÔ∏è They are skilled experts, not just labourers.
    üß∞ Hiring them empowers families and communities.
  `;

  return (
    <div
      style={{
        position: 'relative',
        width: '100vw',
        marginLeft: 'calc(-50vw + 50%)',
        background: 'linear-gradient(to bottom, #0d001c, #000)',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-start',
        paddingLeft: '3%',
        color: 'white',
        fontFamily: 'Arial, sans-serif',
        overflow: 'hidden',
      }}
    >
      {/* TOP CENTER TEXT */}
      <div
        style={{
          position: 'absolute',
          top: '20px',
          left: '50%',
          transform: 'translateX(-50%)',
          textAlign: 'center',
          maxWidth: '900px',
          zIndex: 20,
        }}
      >
        <h2 style={{ fontSize: '2rem', fontWeight: 'bold', marginBottom: '0.5rem' }}>
          Welcome to Our Worker Platform
        </h2>
        <p style={{ fontSize: '1rem', color: '#ccc' }}>
          Discover and connect with skilled professionals near you to get your work done efficiently.
        </p>
      </div>

      {/* LEFT TEXT SECTION */}
      <div style={{ maxWidth: '650px', zIndex: 10, marginLeft: '3rem' }}>
        <p
          style={{
            marginTop: '1rem',
            color: blink ? '#FDE047' : '#aaa', // blinking color
            fontSize: '1.3rem',
            fontWeight: '500',
            whiteSpace: 'pre-line', // preserve line breaks
            transition: 'color 0.5s ease-in-out',
          }}
        >
          {factsParagraph}
        </p>
      </div>

      {/* RIGHT IMAGE */}
      <div style={{ flex: 1, display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <img
          src={workersImage}
          alt="Workers"
          style={{
            maxWidth: '90%',
            maxHeight: '90vh',
            width: 'auto',
            height: 'auto',
          }}
        />
      </div>
    </div>
  );
};

export default HeroSection;
