import React, { useEffect, useState } from 'react';
import TrackWorker from './TrackWorker';

const WorkersList = () => {
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [formData, setFormData] = useState({ location: '', doorNumber: '' });
  const [distance, setDistance] = useState('');
  const [arrivalTime, setArrivalTime] = useState('');
  const [bookedWorkerIds, setBookedWorkerIds] = useState([]);
  const [userCoords, setUserCoords] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const [showBookingPopup, setShowBookingPopup] = useState(false);
  const [showMessagePopup, setShowMessagePopup] = useState(false);
  const [userId, setUserId] = useState(null);

  // Get user from localStorage
  useEffect(() => {
    try {
      const storedUser = localStorage.getItem('user');
      if (storedUser && storedUser !== 'undefined') {
        const loggedInUser = JSON.parse(storedUser);
        setUserId(loggedInUser._id);
      }
    } catch (err) {
      console.error('Invalid user data in localStorage:', err);
    }
  }, []);

  // Fetch workers and location
  useEffect(() => {
    const fetchWorkers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/workers');
        if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
        const data = await res.json();
        setWorkers(data);
      } catch (err) {
        console.error('Failed to fetch workers:', err);
      }
    };

    const fetchUserLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            setUserCoords({
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            });
          },
          (error) => {
            console.error('Geolocation error:', error);
            alert('Please allow location access for better experience.');
          }
        );
      }
    };

    fetchWorkers();
    fetchUserLocation();
  }, []);

  useEffect(() => {
    document.body.style.overflow = showMap ? 'hidden' : 'auto';
  }, [showMap]);

  const handleBookClick = (worker) => {
    setSelectedWorker(worker);
    setFormData({ location: '', doorNumber: '' });
    setDistance('');
    setArrivalTime('');
    setShowBookingPopup(true);
    setShowMap(false);
  };

  const handleBookingFormChange = (e) => {
    const { name, value } = e.target;
    const updatedFormData = { ...formData, [name]: value };
    setFormData(updatedFormData);

    if (updatedFormData.location && updatedFormData.doorNumber) {
      const randomDistance = (Math.random() * (10 - 2) + 2).toFixed(2);
      const randomTime = Math.floor(Math.random() * (25 - 10) + 10);
      setDistance(`${randomDistance} km`);
      setArrivalTime(`${randomTime} minutes`);
    } else {
      setDistance('');
      setArrivalTime('');
    }
  };

  const handleConfirmBooking = async () => {
    if (!formData.location || !formData.doorNumber) {
      alert('Please enter location and door number.');
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/api/workers/${selectedWorker._id}/book`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (res.ok) {
        alert('Booking confirmed!');
        setWorkers((prev) =>
          prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: false } : w))
        );
        setBookedWorkerIds((prev) => [...prev, selectedWorker._id]);
        setShowBookingPopup(false);
        setTimeout(() => {
          setWorkers((prev) =>
            prev.map((w) => (w._id === selectedWorker._id ? { ...w, available: true } : w))
          );
          setBookedWorkerIds((prev) => prev.filter((id) => id !== selectedWorker._id));
        }, 5 * 60 * 1000);
      }
    } catch (err) {
      alert('Booking failed. Please try again.');
    }
  };

  const handleContact = () => {
    if (selectedWorker?.phoneNumber) {
      const telLink = `tel:${selectedWorker.phoneNumber}`;
      window.open(telLink, '_self');
    } else {
      alert('Phone number not available.');
    }
  };

  const handleClosePopup = () => {
    setShowBookingPopup(false);
    setShowMap(false);
    setShowMessagePopup(false);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Available Workers</h2>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
          gap: '20px',
        }}
      >
        {workers.map((worker) => (
          <div
            key={worker._id}
            style={{
              border: '1px solid #ddd',
              borderRadius: '10px',
              padding: '15px',
              boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
            }}
          >
            <div style={{ textAlign: 'center' }}>
              {worker.photo ? (
                <img
                  src={`http://localhost:5000/uploads/${worker.photo}`}
                  alt={worker.name}
                  style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    objectFit: 'cover',
                    border: '2px solid #eee',
                  }}
                />
              ) : (
                <div
                  style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    backgroundColor: '#f0f0f0',
                    margin: '0 auto',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: '#888',
                  }}
                >
                  No Photo
                </div>
              )}
              <h3>{worker.name}</h3>
              <p><strong>Skill:</strong> {worker.workSpecification || 'N/A'}</p>
              <p><strong>Location:</strong> {worker.location || 'N/A'}</p>
              <p style={{ color: worker.available !== false ? 'green' : 'red' }}>
                {worker.available !== false ? 'Available' : 'Booked'}
              </p>
              <button
                onClick={() => handleBookClick(worker)}
                disabled={worker.available === false}
                style={{
                  padding: '8px 15px',
                  backgroundColor: worker.available !== false ? '#4CAF50' : '#ccc',
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: worker.available !== false ? 'pointer' : 'not-allowed',
                }}
              >
                {worker.available !== false ? 'Book Now' : 'Unavailable'}
              </button>
            </div>
          </div>
        ))}
      </div>

      {showBookingPopup && selectedWorker && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', backgroundColor: 'rgba(0,0,0,0.6)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000 }}>
          <div style={{ backgroundColor: 'white', borderRadius: '10px', padding: '20px', width: '90vw', height: '90vh', overflow: 'auto', position: 'relative' }}>
            <button onClick={handleClosePopup} style={{ position: 'absolute', top: '10px', right: '10px', fontSize: '24px', border: 'none', background: 'none' }}>×</button>
            <h3>Booking {selectedWorker.name}</h3>
            <input type="text" name="location" value={formData.location} onChange={handleBookingFormChange} placeholder="Your Address" />
            <input type="text" name="doorNumber" value={formData.doorNumber} onChange={handleBookingFormChange} placeholder="Door Number" />
            {distance && <p><strong>Distance:</strong> {distance}</p>}
            {arrivalTime && <p><strong>ETA:</strong> {arrivalTime}</p>}
            <button onClick={handleConfirmBooking}>Confirm Booking</button>
            <button onClick={() => setShowMap(true)}>View Location</button>
            <button onClick={handleContact}>Call Worker</button>
          </div>
        </div>
      )}

      {showMap && selectedWorker && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', backgroundColor: '#fff', zIndex: 2000 }}>
          <button onClick={() => setShowMap(false)} style={{ padding: '10px', backgroundColor: '#f44336', color: '#fff' }}>Close Map</button>
          <TrackWorker worker={selectedWorker} userCoords={userCoords} />
        </div>
      )}
    </div>
  );
};

export default WorkersList;
