import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Inbox = () => {
  const [messages, setMessages] = useState([]);
  const user = JSON.parse(localStorage.getItem('user'));
  const userId = user._id;
  const model = user.role === 'worker' ? 'Worker' : 'User';

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/messages/inbox/${userId}/${model}`);
        setMessages(res.data);
      } catch (error) {
        console.error('Error fetching inbox:', error);
      }
    };

    fetchMessages();
  }, [userId, model]);

  return (
    <div style={styles.container}>
      <h2>Inbox</h2>
      {messages.length === 0 ? (
        <p>No messages received.</p>
      ) : (
        messages.map((msg, idx) => (
          <div key={idx} style={styles.messageBox}>
            <strong>From:</strong> {msg.senderId.name} <br />
            <strong>Email:</strong> {msg.senderId.email} <br />
            <strong>Message:</strong> {msg.content} <br />
            <small>{new Date(msg.timestamp).toLocaleString()}</small>
          </div>
        ))
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '600px',
    margin: 'auto',
  },
  messageBox: {
    border: '1px solid #ddd',
    padding: '15px',
    borderRadius: '8px',
    marginBottom: '15px',
    background: '#f9f9f9',
  },
};

export default Inbox;
