import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Message = ({ receiverId, onClose }) => {
  const [content, setContent] = useState('');
  const [senderId, setSenderId] = useState('');
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSenderId = async () => {
      const user = JSON.parse(localStorage.getItem('user'));

      if (user?.email) {
        try {
          const res = await axios.get(`http://localhost:5000/api/users/email/${user.email}`);
          setSenderId(res.data._id);
        } catch (err) {
          console.error('❌ Error fetching sender ID:', err.response?.data || err.message);
          setStatus('Failed to load sender information');
        } finally {
          setLoading(false);
        }
      } else {
        console.error('❌ Email missing in localStorage');
        setStatus('You are not logged in');
        setLoading(false);
      }
    };

    fetchSenderId();
  }, []);

  const sendMessage = async () => {
    if (!content.trim()) {
      setStatus('⚠️ Please type a message.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/messages', {
        senderId,
        receiverId,
        content,
      });

      setStatus('✅ Message sent!');
      setContent('');
    } catch (error) {
      console.error('❌ Failed to send message:', error.response?.data || error.message);
      setStatus('❌ Failed to send message.');
    }
  };

  if (loading) return <p>Loading sender info...</p>;

  return (
    <div className="message-popup" style={{ padding: '1rem', border: '1px solid #ccc', background: '#fff' }}>
      <textarea
        placeholder="Type your message..."
        value={content}
        onChange={(e) => setContent(e.target.value)}
        rows={4}
        style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
      />
      <div style={{ display: 'flex', gap: '1rem' }}>
        <button onClick={sendMessage}>Send</button>
        <button onClick={onClose}>Close</button>
      </div>
      {status && <p style={{ marginTop: '0.5rem', color: status.includes('❌') ? 'red' : 'green' }}>{status}</p>}
    </div>
  );
};

export default Message;
