import React, { useState, useContext, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { useLoadScript } from '@react-google-maps/api';

const libraries = ['places'];

const Signup = () => {
  const { isLoaded } = useLoadScript({
    googleMapsApiKey: 'YOUR_GOOGLE_MAPS_API_KEY', // replace with your key
    libraries,
  });

  const [formData, setFormData] = useState({
    role: 'user',
    email: '',
    password: '',
    name: '',
    workSpecification: '',
    experience: '',
    location: '',
    phoneNumber: '',
    photo: null,
  });

  const autocompleteRef = useRef(null);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);

  const handlePlaceChange = () => {
    const place = autocompleteRef.current.getPlace();
    setFormData((prev) => ({
      ...prev,
      location: place.formatted_address,
    }));
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const form = new FormData();
      Object.entries(formData).forEach(([key, value]) => {
        if (key === 'photo' && formData.role !== 'worker') return;
        if (value) form.append(key, value);
      });

      const res = await fetch('http://localhost:5000/api/auth/signup', {
        method: 'POST',
        body: form,
      });

      const data = await res.json();

      if (res.ok) {
        login({ username: formData.name, role: formData.role });
        navigate('/');
      } else {
        setMessage(data.message || 'Signup failed');
      }
    } catch (error) {
      setMessage('Error connecting to server');
    }
  };

  if (!isLoaded) return <div>Loading...</div>;

  return (
    <div style={styles.container}>
      <h2>Signup</h2>
      <form onSubmit={handleSignup} style={styles.form}>
        <select name="role" value={formData.role} onChange={handleChange} style={styles.input}>
          <option value="user">User</option>
          <option value="worker">Worker</option>
        </select>

        <input
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
          style={styles.input}
          type="email"
        />

        <input
          name="password"
          type="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
          style={styles.input}
        />

        <input
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
          style={styles.input}
        />

        <input
          name="phoneNumber"
          placeholder="Phone Number"
          value={formData.phoneNumber}
          onChange={handleChange}
          required
          style={styles.input}
          type="tel"
        />

        {/* Google Places Autocomplete input */}
        <Autocomplete
          onLoad={(autocomplete) => (autocompleteRef.current = autocomplete)}
          onPlaceChanged={handlePlaceChange}
        >
          <input
            name="location"
            placeholder="Search Location"
            value={formData.location}
            onChange={handleChange}
            required
            style={styles.input}
          />
        </Autocomplete>

        {/* Worker-specific fields */}
        {formData.role === 'worker' && (
          <>
            <input
              name="workSpecification"
              placeholder="Work Specialization"
              value={formData.workSpecification}
              onChange={handleChange}
              required
              style={styles.input}
            />
            <input
              name="experience"
              placeholder="Experience (e.g. 3 years)"
              value={formData.experience}
              onChange={handleChange}
              required
              style={styles.input}
            />
            <input
              name="photo"
              type="file"
              accept="image/*"
              onChange={handleChange}
              required
              style={styles.input}
            />
          </>
        )}

        <button type="submit" style={styles.button}>Sign Up</button>
      </form>
      {message && <p style={{ color: 'red' }}>{message}</p>}
    </div>
  );
};

// Add Autocomplete import at the top
import { Autocomplete } from '@react-google-maps/api';

const styles = {
  container: {
    maxWidth: '500px',
    margin: '2rem auto',
    padding: '2rem',
    boxShadow: '0 0 10px #ccc',
    borderRadius: '8px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  input: {
    padding: '0.7rem',
    marginBottom: '1rem',
    fontSize: '1rem',
  },
  button: {
    padding: '0.7rem',
    backgroundColor: '#27ae60',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
  },
};

export default Signup;
