import React from 'react';

const Home = () => {
  return (
    <div style={styles.container}>
      {/* Background Video */}
      <video
        autoPlay
        loop
        muted
        style={styles.backgroundVideo}
      >
        <source src="/bluecol.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Content */}
      <h1>Welcome to My MERN Auth Project</h1>
      <p>Secure authentication using email verification.</p>
    </div>
  );
};

const styles = {
  container: {
    position: 'relative',       // Needed to position video absolutely inside
    height: '80vh',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: 'white',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textShadow: '2px 2px 4px #000',
    overflow: 'hidden',          // To hide anything outside container
  },
  backgroundVideo: {
    position: 'absolute',        // Cover the whole container
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    objectFit: 'cover',          // Make sure video covers entire area
    zIndex: -1,                  // Send video behind text content
  },
};

export default Home;
