import React, { useState } from 'react';
import axios from 'axios';

const Message = ({ receiverId, receiverModel, onClose }) => {
  const [content, setContent] = useState('');

  // Get sender info from localStorage/session (or context)
  const sender = JSON.parse(localStorage.getItem('user'));
  const senderId = sender._id;
  const senderModel = sender.role === 'worker' ? 'Worker' : 'User';

  const handleSend = async () => {
    try {
      await axios.post('http://localhost:5000/api/messages', {
  senderId: user._id,
  receiverId,
  senderModel: user.role === 'worker' ? 'Worker' : 'User',
  receiverModel,
  content,
}, {
  headers: {
    Authorization: `Bearer ${localStorage.getItem('token')}`,
  },
});
      alert('Message sent successfully!');
      setContent('');
      onClose(); // Close popup
    } catch (err) {
      console.error('Failed to send message:', err);
      alert('Failed to send message.');
    }
  };

  return (
    <div style={styles.popup}>
      <h3>Send Message</h3>
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Type your message..."
        style={styles.textarea}
      />
      <div style={styles.actions}>
        <button onClick={handleSend} style={styles.button}>Send</button>
        <button onClick={onClose} style={styles.cancel}>Cancel</button>
      </div>
    </div>
  );
};

const styles = {
  popup: {
    background: '#fff',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 0 10px #aaa',
    width: '300px',
    position: 'fixed',
    top: '30%',
    left: '40%',
    zIndex: 999,
  },
  textarea: {
    width: '100%',
    height: '100px',
    padding: '10px',
  },
  actions: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '10px',
  },
  button: {
    padding: '8px 12px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
  },
  cancel: {
    padding: '8px 12px',
    backgroundColor: '#ccc',
    color: 'black',
    border: 'none',
    cursor: 'pointer',
  },
};

export default Message;
